package com.org.acpgimini.datatypesandvariables;

public class RelationalOp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 97;
		int j = 45;
		System.out.println("i is less thaa j :" + (i < j));
		System.out.println("j is ggreater than i :" + (j > i));
		System.out.println("i is equals to j :" + (i == j));
		System.out.println("i is greater than equals to j" + (i >= j));
		System.out.println("i is less thhan equal to j" + (i <= j));
		System.out.println("i is not equals to j" + (i != j));

	}

}
